enum class ElementCategory(val title: String) {
    Animal("Animal"),
    Flower("Flower"),
    Fruit("Fruit")
}

data class Element(
    val id: Int,
    val name: String,
    val emoji: String,
    val dominantColor: String,
    val description: String
)
